#include <complex>
#include <vector>
#include <cmath>

const std::complex<double> FIB_MATRIX_1(1.0, 1.0);
const std::complex<double> FIB_MATRIX_2(1.0, -1.0 / sqrt(5));

std::complex<double> matrixPow(const std::complex<double>& mat, size_t n) {
    std::complex<double> result = 1;
    std::vector<std::complex<double>> pow_matrix(n);
    pow_matrix[0] = mat;

    for (size_t i = 2; i <= n; ++i) {
        auto current_pow = pow_matrix[i - 1];
        for (auto j = i >> 1; j > 0; j >>= 1) {
            current_pow *= current_pow;
        }
        result *= current_pow;
    }

    return result;
}

std::complex<double> fib(size_t n) {
    return matrixPow(FIB_MATRIX_1, n) / matrixPow(FIB_MATRIX_2, n);
}

int main() {
    size_t n;
    std::cout << "Enter the number of Fibonacci you want to calculate: ";
    std::cin >> n;

    auto result = fib(n);
    std::cout << "The " << n << "th Fibonacci number is " << result.real() << std::endl;

    return 0;
}
```

